* {
  margin: 0;
  padding: 0;
}
:root {
  --huge-black: #000;
  --huge-grey: #808080;
  --huge-grey-dark: #232525;
  --huge-magenta: #ec008c;
  --huge-white: #fff;
  --huge-blue: #2fbbf9;
}
/*

*/
body {
  background: var(--huge-white);
  font-family: "HUGEAvantGarde", Georgia;
}

h1 {
  font-family: 'Mountains of Christmas', Arial, Helvetica, sans-serif, cursive;
  font-size: 7.5rem;
}
h2 {
  font-size: 7rem;
}
h3 {
  font-size: 6rem;
}
h4 {
  font-size: 5rem;
}
h5 {
  font-size: 4rem;
}
h6 {
  font-size: 3rem;
}
p {
  font-size: 2rem;
}

h1,h2,h3,h4,h5,h6, p {
  color: var(--huge-black);
}

.container {
  display: flex;
  flex-flow: column nowrap;
}

.menu {
  margin: 50px 0 150px;
  text-align: center;
}

.footer {
  align-content: center;
  display: flex;
  height: 50px;
  justify-content: center;
  margin: 100px 0 0;
  text-align: center;
}

.bigh {
  background: red;
}

.santa {
  height: 50px;
  position: absolute;
  top: 0;
  left: 500px;
}

.letter .wrapper {
  margin: 0 auto;
  width: 80%;
}

.wrapper {
  position: relative;
}

.subtitle {
  margin: 50px 0;
}

.smalltext {
  font-size: .8rem;
  margin-top: 30px;
}

.button {
	cursor: pointer;
	text-decoration: none !important;
	display: inline-block;
	font-family: Arial,sans-serif;
	font-weight: bold;
	text-align: center;
	color: #fff;
	-webkit-transition: background-color 0.3s,opacity 0.3s;
	-moz-transition: background-color 0.3s,opacity 0.3s;
	-o-transition: background-color 0.3s,opacity 0.3s;
	transition: background-color 0.3s,opacity 0.3s;
}

.button.load {
	-webkit-transition: 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275) all;
	-moz-transition: 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275) all;
	-o-transition: 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275) all;
	transition: 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275) all;
}

.button.black {
	background: var(--huge-black);
	width: 160px;
	height: 54px;
	line-height: 54px;
	font-size: 13px;
}

.button:hover {
	background-color: #ec008c !important;
	color: #fff;
}
